import React from 'react';
import { ConversationalForm } from 'conversational-form';
import PythonBridge from 'python-bridge';
const py = new PythonBridge();

export default class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.formFields = [
      {
        'tag': 'input',
        'type': 'text',
        'name': 'firstname',
        'cf-questions': 'What is your firstname?'
      },
      {
        'tag': 'input',
        'type': 'text',
        'name': 'lastname',
        'cf-questions': 'What is your lastname?'
      }
    ];

    this.submitCallback = this.submitCallback.bind(this);
  }

  componentDidMount() {
    this.cf = ConversationalForm.startTheConversation({
      options: {
        submitCallback: this.submitCallback,
        preventAutoFocus: true,
        // loadExternalStyleSheet: false
      },
      tags: this.formFields
    });
    this.elem.appendChild(this.cf.el);
  }

  submitCallback() {
    var formDataSerialized = this.cf.getFormData(true);
    const firstname = formDataSerialized.firstname;
    const lastname = formDataSerialized.lastname;
    py.expose({
      chain: (firstname, lastname) => {
        return { answer: `Hello ${firstname} ${lastname}` , sources: 'local' };
      }
    });

    py.run('chain', firstname, lastname).then(answer => {
      this.cf.addRobotChatResponse(`Answer: ${answer.answer}\nSources: ${answer.sources}`);
    });
  }

  render() {
    return (
      <div>
        <div
          ref={ref => this.elem = ref}
        />
      </div>
    );
  }
}
